import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

// 새로운 유틸리티 함수 추가
const formatPrice = (price, currency) => {
  if (currency === 'KRW') {
    return new Intl.NumberFormat('ko-KR', { style: 'decimal' }).format(price);
  }
  return new Intl.NumberFormat('en-US', { style: 'decimal' }).format(price);
};

const calculateChange = (current, previous) => {
  if (!previous || previous === 0) return { percentage: 0 };

  const currentPrice = typeof current === 'string' ? parseFloat(current.replace(/,/g, '')) : current;
  const previousPrice = typeof previous === 'string' ? parseFloat(previous.replace(/,/g, '')) : previous;

  const percentage = ((currentPrice - previousPrice) / previousPrice * 100).toFixed(2);
  return { percentage: parseFloat(percentage) };
};

const CoinRow = React.memo(({ coin }) => {
  console.log(`CoinRow 컴포넌트 리렌더링: ${coin.symbol}`);
  console.log('History Data:', coin.history); // history 데이터 로깅

  return (
    <tr>
      <td>{coin.symbol}</td>
      <td>{formatPrice(coin.upbitPrice, 'KRW')}</td>
      <td>{formatPrice(coin.binancePrice, 'USD')}</td>
      <td>{coin.kimchiPremium}%</td>
      <td className={`price-change ${coin.price_change_percentage_24h > 0 ? 'price-up' : 'price-down'}`}>
        {coin.price_change_percentage_24h ? coin.price_change_percentage_24h.toFixed(2) : 'N/A'}
      </td>
      <td className={`price-change ${coin.price_change_percentage_7d_in_currency > 0 ? 'price-up' : 'price-down'}`}>
        {coin.price_change_percentage_7d_in_currency ? coin.price_change_percentage_7d_in_currency.toFixed(2) : 'N/A'}
      </td>
      <td className={`price-change ${coin.price_change_percentage_30d_in_currency > 0 ? 'price-up' : 'price-down'}`}>
        {coin.price_change_percentage_30d_in_currency ? coin.price_change_percentage_30d_in_currency.toFixed(2) : 'N/A'}
      </td>
      <td className={`price-change ${coin.price_change_percentage_180d_in_currency > 0 ? 'price-up' : 'price-down'}`}>
        {coin.price_change_percentage_180d_in_currency ? coin.price_change_percentage_180d_in_currency.toFixed(2) : 'N/A'}
      </td>
      <td className={`price-change ${coin.price_change_percentage_365d_in_currency > 0 ? 'price-up' : 'price-down'}`}>
        {coin.price_change_percentage_365d_in_currency ? coin.price_change_percentage_365d_in_currency.toFixed(2) : 'N/A'}
      </td>
    </tr>
  );
}, (prevProps, nextProps) => {
  if (prevProps.coin !== nextProps.coin) {
    return false;
  }
  return true;
});

function App() {
  const [coinPrices, setCoinPrices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/coins');
        setCoinPrices(prevPrices => {
          const newPrices = response.data;
          if (JSON.stringify(prevPrices) !== JSON.stringify(newPrices)) {
            return newPrices;
          }
          return prevPrices;
        });
      } catch (error) {
        console.error('API 호출 에러:', error);
        setError('시세 정보를 불러오는 중 오류가 발생했습니다.');
      } finally {
        if (loading) {
          setLoading(false);
        }
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 15000);

    return () => clearInterval(intervalId);
  }, [loading]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img
          src="/Images/header-background-small.jpg"
          srcSet="/Images/header-background-small.jpg 600w, /Images/header-background-large.jpg 1200w"
          alt="Header Background"
          className="header-image"
        />
      </header>
      <main>
        <table>
          <thead>
            <tr>
              <th>Symbol</th>
              <th>Upbit Price (KRW)</th>
              <th>Binance Price (USD)</th>
              <th>김프</th>
              <th>전일대비</th>
              <th>7일대비</th>
              <th>30일대비</th>
              <th>180일대비</th>
              <th>360일대비</th>
            </tr>
          </thead>
          <tbody>
            {coinPrices.map(coin => (
              <CoinRow key={coin.symbol} coin={coin} />
            ))}
          </tbody>
        </table>
      </main>
      <footer>
        <p>&copy; 2024 Crypto Price Tracker</p>
      </footer>
    </div>
  );
}

export default App;
