import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

// 새로운 유틸리티 함수 추가
const formatPrice = (price, currency) => {
  if (currency === 'KRW') {
    return new Intl.NumberFormat('ko-KR', { style: 'decimal' }).format(price);
  }
  return new Intl.NumberFormat('en-US', { style: 'decimal' }).format(price);
};

const calculateChange = (current, previous) => {
  if (!previous || previous === 0) return { percentage: 0 };

  const currentPrice = typeof current === 'string' ? parseFloat(current.replace(/,/g, '')) : current;
  const previousPrice = typeof previous === 'string' ? parseFloat(previous.replace(/,/g, '')) : previous;

  const percentage = ((currentPrice - previousPrice) / previousPrice * 100).toFixed(2);
  return { percentage: parseFloat(percentage) };
};

const CoinRow = React.memo(({ coin, coinIcons }) => {
  console.log(`CoinRow 컴포넌트 리렌더링: ${coin.symbol}`);
  console.log('History Data:', coin.history); // history 데이터 로깅

  const iconPath = `/icons/${coin.symbol.toLowerCase()}.png`; // 로컬 이미지 경로 (현재는 사용 안함)
  const coinSymbolUpperCase = coin.symbol.toUpperCase();
  const iconUrl = coinIcons[coinSymbolUpperCase];
  const hasIcon = !!iconUrl; // 아이콘 URL 존재 여부 확인
  const [iconLoading, setIconLoading] = useState(true); // 아이콘 로딩 상태 추가
  const [iconError, setIconError] = useState(false); // 아이콘 로드 실패 상태 추가

  useEffect(() => {
    if (hasIcon) {
      setIconLoading(true); // 아이콘 로딩 시작
      setIconError(false); // 에러 상태 초기화
    }
  }, [hasIcon]); // iconUrl (coinIcons 상태) 이 변경될 때마다 실행

  const handleIconLoad = () => {
    setIconLoading(false); // 아이콘 로딩 완료
  };

  const handleIconError = () => {
    setIconLoading(false); // 로딩 완료 (에러 발생)
    setIconError(true); // 에러 상태 설정
  };

  return (
    <tr>
      <td>
        {hasIcon ? ( // 아이콘 URL 이 있는 경우에만 이미지 표시
          <>
            {iconLoading && !iconError && <span style={{ marginRight: '5px' }}>Loading...</span>} {/* 로딩 중 표시 */}
            {iconError && <span style={{ marginRight: '5px', color: 'red' }}>🚫</span>} {/* 에러 발생 시 오류 아이콘 표시 */}
            <img
              src={iconUrl}
              alt={`${coin.symbol} icon`}
              style={{
                width: '24px',
                height: '24px',
                marginRight: '5px',
                verticalAlign: 'middle',
                display: iconLoading || iconError ? 'none' : 'inline' // 로딩 중이거나 에러 시 숨김
              }}
              onLoad={handleIconLoad} // 로딩 성공 핸들러
              onError={handleIconError} // 로딩 실패 핸들러
            />
          </>
        ) : (
          <span style={{ marginRight: '5px' }}>No Icon</span> // 아이콘 URL 없는 경우 텍스트 표시
        )}
        {coin.symbol}
      </td>
      <td>{formatPrice(coin.upbitPrice, 'KRW')}</td>
      <td>{formatPrice(coin.binancePrice, 'USD')}</td>
      <td>{coin.kimchiPremium}%</td>
      <td className={`price-change ${calculateChange(coin.upbitPrice, coin.history?.day1).percentage < 0 ? 'price-down' : 'price-up'}`}>
        {coin.history?.day1 ? `${calculateChange(coin.upbitPrice, coin.history.day1).percentage.toFixed(2)}%` : 'N/A'}
      </td>
      <td className={`price-change ${calculateChange(coin.upbitPrice, coin.history?.day7).percentage < 0 ? 'price-down' : 'price-up'}`}>
        {coin.history?.day7 ? `${calculateChange(coin.upbitPrice, coin.history.day7).percentage.toFixed(2)}%` : 'N/A'}
      </td>
      <td className={`price-change ${calculateChange(coin.upbitPrice, coin.history?.day30).percentage < 0 ? 'price-down' : 'price-up'}`}>
        {coin.history?.day30 ? `${calculateChange(coin.upbitPrice, coin.history.day30).percentage.toFixed(2)}%` : 'N/A'}
      </td>
      <td className={`price-change ${calculateChange(coin.upbitPrice, coin.history?.day180).percentage < 0 ? 'price-down' : 'price-up'}`}>
        {coin.history?.day180 ? `${calculateChange(coin.upbitPrice, coin.history.day180).percentage.toFixed(2)}%` : 'N/A'}
      </td>
      <td className={`price-change ${calculateChange(coin.upbitPrice, coin.history?.day360).percentage < 0 ? 'price-down' : 'price-up'}`}>
        {coin.history?.day360 ? `${calculateChange(coin.upbitPrice, coin.history.day360).percentage.toFixed(2)}%` : 'N/A'}
      </td>
    </tr>
  );
}, (prevProps, nextProps) => {
  if (prevProps.coin !== nextProps.coin || prevProps.coinIcons !== nextProps.coinIcons) {
    return false; // props가 다르면 리렌더링
  }
  return true; // props가 같으면 리렌더링 방지
});

function App() {
  const [coinPrices, setCoinPrices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [coinIcons, setCoinIcons] = useState({}); // 코인 아이콘 URL 저장 상태 추가
  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    const fetchCoinList = async () => { // CoinGecko API 호출 함수
      try {
        const response = await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=krw&order=market_cap_desc&per_page=250&page=1&sparkline=false'); // per_page, page 조정
        const icons = {};
        response.data.forEach(coin => {
          icons[coin.symbol.toUpperCase()] = coin.image; // 심볼을 키로, 이미지 URL을 값으로 저장 (대문자 통일)
        });
        setCoinIcons(icons);
      } catch (error) {
        console.error('CoinGecko API 호출 에러:', error);
      }
    };

    const fetchCoinPrices = async () => { // 기존 API 호출 함수 (이름 변경)
      try {
        const response = await axios.get('http://localhost:5001/api/coins');
        setCoinPrices(prevPrices => {
          const newPrices = response.data;
          if (JSON.stringify(prevPrices) !== JSON.stringify(newPrices)) {
            return newPrices;
          }
          return prevPrices;
        });
      } catch (error) {
        console.error('API 호출 에러:', error);
        setError('시세 정보를 불러오는 중 오류가 발생했습니다.');
      } finally {
        if (loading) {
          setLoading(false);
        }
      }
    };

    fetchCoinList(); // CoinGecko API 먼저 호출 (아이콘 정보 가져오기)
    fetchCoinPrices(); // 기존 API 호출

    const intervalId = setInterval(fetchCoinPrices, 15000);

    return () => clearInterval(intervalId);
  }, [loading]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img
          src="/Images/header-background-small.jpg"
          srcSet="/Images/header-background-small.jpg 600w, /Images/header-background-large.jpg 1200w"
          alt="Header Background"
          className="header-image"
        />
      </header>
      <main>
        <table>
          <thead>
            <tr>
              <th>Symbol</th>
              <th>Upbit Price (KRW)</th>
              <th>Binance Price (USD)</th>
              <th>김프</th>
              <th>전일대비</th>
              <th>7일대비</th>
              <th>30일대비</th>
              <th>180일대비</th>
              <th>360일대비</th>
            </tr>
          </thead>
          <tbody>
            {coinPrices.map(coin => (
              <CoinRow key={coin.symbol} coin={coin} coinIcons={coinIcons} />
            ))}
          </tbody>
        </table>
      </main>
      <footer>
        <p>&copy; 2024 Crypto Price Tracker</p>
      </footer>
    </div>
  );
}

export default App;
